import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';

import {Button, Icon} from '@collab-ui/react';

import styles from './styles.css';

const propTypes = {
  activityTypes: PropTypes.array.isRequired,
  onClose: PropTypes.func.isRequired,
  onMenuClick: PropTypes.func.isRequired,
  title: PropTypes.string
};

const defaultProps = {
  title: ''
};

function ActivityMenuHeader({
  activityTypes,
  onClose,
  onMenuClick,
  title
}) {
  const displayMenu = activityTypes && activityTypes.length > 2;
  return (
    <div className={classNames('ciscospark-widget-header', styles.widgetHeader)}>
      <div className={classNames('ciscospark-widget-close', styles.menuButton)}>
        <Icon
          ariaLabel="Close"
          name="icon-cancel_18"
          onClick={onClose}
        />
      </div>
      <div className={classNames('ciscospark-widget-title', styles.widgetTitle)}>
        {title}
      </div>
      <div className={classNames('ciscospark-widget-menu', styles.menuButton)}>
        { displayMenu &&
          <Button
            ariaLabel="Main Menu"
            circle
            iconColor="black"
            onClick={onMenuClick}
            size={32}
          >
            <Icon name="icon-activities_16" />
          </Button>
        }
      </div>
    </div>
  );
}

ActivityMenuHeader.propTypes = propTypes;
ActivityMenuHeader.defaultProps = defaultProps;

export default ActivityMenuHeader;
